const loginForm = document.getElementById("login-form");
loginForm.addEventListener("submit", function(event) {
  event.preventDefault();
  const username = document.getElementById("username").value;
  const password = document.getElementById("password").value;

  if (username === "yaseen" && password === "yaseen") {
    document.getElementById("login-page").style.display = "none";
    document.getElementById("app-container").style.display = "flex";
    fetchMatchData();
  } else {
    document.getElementById("error-message").style.display = "block";
  }
  if ('ontouchstart' in window) {
    loginForm.classList.add('touch-active');
  }
});

const matchCategories = document.getElementById("match-categories");

// ====== Only Fancode Data ======
async function fetchMatchData() {
  try {
    const response1 = await fetch('https://raw.githubusercontent.com/drmlive/fancode-live-events/refs/heads/main/fancode.json');
    const data1 = await response1.json();
    const matches1 = data1.matches.map(match => ({
      ...match,
      category: 'Champions Trophy 2025',
      subcategory: 'Ongoing Matches'
    }));

    const allMatches = [...matches1];
    displayCategories(allMatches);
    renderFeatureBanner(allMatches);
    renderLiveNowCarousel(allMatches);
  } catch (error) {
    console.error("Error fetching match data:", error);
  }
}

function openInPlayerIfM3U8(url) {
  if (url && url.endsWith('.m3u8')) {
    url = `https://yaseenzj.github.io/sportify/player.html?dtv=${encodeURIComponent(url)}`;
  }
  window.open(url, '_blank');
}

function displayCategories(matches) {
  matchCategories.innerHTML = "";
  const categories = {};
  matches.forEach(match => {
    if (!categories[match.category]) categories[match.category] = {};
    if (!categories[match.category][match.subcategory]) categories[match.category][match.subcategory] = [];
    categories[match.category][match.subcategory].push(match);
  });
  Object.keys(categories).forEach(category => {
    const categorySection = document.createElement("div");
    categorySection.className = "category-section";
    const categoryTitle = document.createElement("h2");
    categoryTitle.textContent = category;
    categorySection.appendChild(categoryTitle);
    Object.keys(categories[category]).forEach(subcategory => {
      const subcategoryDiv = document.createElement("div");
      subcategoryDiv.className = "subcategory";
      const subTitle = document.createElement("h3");
      subTitle.textContent = subcategory;
      subTitle.style.fontSize = "1em";
      subTitle.style.fontWeight = "400";
      subTitle.style.margin = "10px 0 8px 0";
      subcategoryDiv.appendChild(subTitle);
      const matchesListWrapper = document.createElement("div");
      matchesListWrapper.className = "matches-list-wrapper";
      categories[category][subcategory].forEach(match => {
        const matchDiv = document.createElement("div");
        matchDiv.className = "match";
        matchDiv.innerHTML = `
          <img src="${match.src}" alt="${match.match_name}">
          <div class="match-overlay"><p>${match.match_name}</p></div>
        `;
        matchDiv.onclick = () => openInPlayerIfM3U8(match.dai_url);
        matchesListWrapper.appendChild(matchDiv);
      });
      subcategoryDiv.appendChild(matchesListWrapper);
      categorySection.appendChild(subcategoryDiv);
    });
    matchCategories.appendChild(categorySection);
  });
}

function renderFeatureBanner(matches) {
  const ongoingMatches = matches.filter(
    m => m.category === "Champions Trophy 2025" || m.category === "Ongoing Matches"
  );
  const featuredMatch = ongoingMatches[0] || matches[0];
  const banner = document.getElementById('feature-banner');
  banner.innerHTML = `
    <div class="banner-bg" style="background-image: url('${featuredMatch.src}');"></div>
    <div class="banner-info">
      <div style="font-size: 0.9em; color: #b3bbc6; letter-spacing: 0.1em; margin-bottom: 4px;">START WATCHING</div>
      <div class="banner-title">${featuredMatch.match_name}</div>
      <div class="banner-meta">LIVE NOW</div>
      <div class="banner-desc">Watch ${featuredMatch.match_name} live in HD quality. Click play to start streaming instantly.</div>
      <div class="banner-meta">${featuredMatch.subcategory || ""}</div>
      <div class="banner-actions">
        <button class="banner-btn" onclick="openInPlayerIfM3U8('${featuredMatch.dai_url}')">PLAY</button>
        <button class="banner-btn secondary">DETAILS</button>
        <button class="banner-btn secondary circular">ðŸ”‡</button>
      </div>
    </div>
  `;
}

function renderLiveNowCarousel(matches) {
  const ongoingMatches = matches.filter(
    m => m.category === "Champions Trophy 2025" || m.category === "Ongoing Matches"
  );
  if (!ongoingMatches.length) return;

  const visibleCount = 5; // Show max 5 at once
  const section = document.getElementById('live-now');
  section.innerHTML = `
    <div class="section-title">LIVE NOW</div>
    <div class="carousel-wrapper">
      <button class="carousel-arrow left" aria-label="Scroll left">&#8249;</button>
      <div class="horizontal-carousel" id="live-carousel"></div>
      <button class="carousel-arrow right" aria-label="Scroll right">&#8250;</button>
    </div>
  `;

  const carousel = document.getElementById('live-carousel');
  ongoingMatches.forEach(match => {
    const card = document.createElement('div');
    card.className = 'carousel-card';
    card.innerHTML = `
      <img src="${match.src}" alt="${match.match_name}">
      <div class="card-title">${match.match_name}</div>
    `;
    card.onclick = () => openInPlayerIfM3U8(match.dai_url);
    carousel.appendChild(card);
  });

  function getVisibleCount() {
    if (window.innerWidth <= 480) return 2;
    if (window.innerWidth <= 768) return 3;
    return visibleCount;
  }
  function updateCarouselWidth() {
    const count = getVisibleCount();
    const card = carousel.querySelector('.carousel-card');
    if (card) {
      carousel.style.maxWidth = `${card.offsetWidth * count + 24 * (count - 1)}px`;
    }
  }
  let scrollIndex = 0;
  const leftArrow = section.querySelector('.carousel-arrow.left');
  const rightArrow = section.querySelector('.carousel-arrow.right');
  function updateArrows() {
    leftArrow.style.display = scrollIndex > 0 ? 'block' : 'none';
    rightArrow.style.display = (scrollIndex + getVisibleCount()) < ongoingMatches.length ? 'block' : 'none';
  }
  function scrollToIndex(index) {
    const card = carousel.querySelector('.carousel-card');
    if (!card) return;
    const cardWidth = card.offsetWidth + 24;
    carousel.scrollTo({
      left: cardWidth * index,
      behavior: 'smooth'
    });
    scrollIndex = index;
    updateArrows();
  }
  leftArrow.addEventListener('click', () => {
    if (scrollIndex > 0) scrollToIndex(scrollIndex - 1);
  });
  rightArrow.addEventListener('click', () => {
    if ((scrollIndex + getVisibleCount()) < ongoingMatches.length) scrollToIndex(scrollIndex + 1);
  });
  window.addEventListener('resize', () => {
    updateCarouselWidth();
    updateArrows();
  });
  setTimeout(() => {
    updateCarouselWidth();
    scrollToIndex(0);
  }, 100);
}

// Live Channels section rendering
function renderLiveChannels() {
  const liveChannelContainer = document.getElementById("liveChannel");
  liveChannelContainer.innerHTML = "";
  liveChannels.forEach(channel => {
    const div = document.createElement("div");
    div.className = "match";
    div.innerHTML = `
      <img src="${channel.src}" alt="${channel.match_name}">
      <div class="match-overlay"><p>${channel.match_name}</p></div>
    `;
    div.onclick = () => openInPlayerIfM3U8(channel.dai_url);
    liveChannelContainer.appendChild(div);
  });
}

// F1 section rendering
function renderF1Videos() {
  const f1Section = document.getElementById("f1-section");
  const f1VideosContainer = document.getElementById("f1-videos");
  f1VideosContainer.innerHTML = "";
  f1Videos.forEach(video => {
    const videoDiv = document.createElement("div");
    videoDiv.className = "match";
    videoDiv.innerHTML = `
      <img src="${video.thumbnail}" alt="${video.title}">
      <div class="match-overlay"><p>${video.title}</p></div>
    `;
    videoDiv.onclick = () => openInPlayerIfM3U8(video.url);
    f1VideosContainer.appendChild(videoDiv);
  });
}

// Sidebar navigation handlers
document.addEventListener("DOMContentLoaded", function() {
  // TV icon
  document.getElementById("sidebar-live").addEventListener("click", function() {
    document.getElementById("feature-banner").style.display = "none";
    document.getElementById("live-now").style.display = "none";
    document.getElementById("match-categories").style.display = "none";
    document.getElementById("f1-section").style.display = "none";
    document.getElementById("live-section").style.display = "block";
    renderLiveChannels();
    document.querySelectorAll('.sidebar-icons span').forEach(span => span.classList.remove('active'));
    this.classList.add('active');
  });

  // Home icon
  document.getElementById("sidebar-home").addEventListener("click", function() {
    document.getElementById("feature-banner").style.display = "block";
    document.getElementById("live-now").style.display = "block";
    document.getElementById("match-categories").style.display = "block";
    document.getElementById("live-section").style.display = "none";
    document.getElementById("f1-section").style.display = "none";
    document.querySelectorAll('.sidebar-icons span').forEach(span => span.classList.remove('active'));
    this.classList.add('active');
  });

  // F1 icon
  document.getElementById("sidebar-f1").addEventListener("click", function() {
    document.getElementById("feature-banner").style.display = "none";
    document.getElementById("live-now").style.display = "none";
    document.getElementById("match-categories").style.display = "none";
    document.getElementById("live-section").style.display = "none";
    document.getElementById("f1-section").style.display = "block";
    renderF1Videos();
    document.querySelectorAll('.sidebar-icons span').forEach(span => span.classList.remove('active'));
    this.classList.add('active');
  });
});

// For any <a href="...m3u8"> links in the DOM
document.addEventListener('DOMContentLoaded', function() {
  document.querySelectorAll('a[href$=".m3u8"]').forEach(link => {
    link.addEventListener('click', function(event) {
      event.preventDefault();
      const originalUrl = this.getAttribute('href');
      const playerUrl = `https://yaseenzj.github.io/sportify/player.html?dtv=${encodeURIComponent(originalUrl)}`;
      window.open(playerUrl, '_blank');
    });
  });
});